  background: var\(--bg-card\);
  border: 1px solid var\(--border-color\);
  border-radius: 20px;
  padding: 44px;
  box-shadow: var\(--card-shadow\);
  position: relative;
  overflow: hidden;
}

.appointment-content::before {
  content: '';
  position: absolute;
  top: 0; left: 0; right: 0;
  height: 3px;
  background: var\(--accent-gradient\);
}

.appointment-form-title {
  font-family: 'Playfair Display', serif;
  font-size: 1.3rem;
  color: var\(--text-heading\);
  margin-bottom: 28px;
}

.appointment-form {
  display: flex;
  flex-direction: column;
  gap: 0;
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
  margin-bottom: 16px;
}

.form-group {
  position: relative;
  margin-bottom: 0;
}

.appointment-form input,
.appointment-form select,
.appointment-form textarea {
  width: 100%;
  padding: 13px 18px;
  border: 1px solid var\(--border-color\);
  border-radius: 10px;
  font-size: 0.95rem;
  font-family: inherit;
  transition: all 0.3s ease;
  background: var\(--bg-input\);
  color: var\(--text-primary\);
}

.appointment-form input::placeholder,
.appointment-form textarea::placeholder {
  color: var\(--text-secondary\);
}

.appointment-form input:focus,
.appointment-form select:focus,
.appointment-form textarea:focus {
  outline: none;
  border-color: var\(--green\);
  box-shadow: 0 0 0 3px var\(--green-light\);
}

.appointment-form .form-group:not\(:last-child\) {
  margin-bottom: 16px;
}

.appointment-form textarea {
  resize: vertical;
  margin-bottom: 20px;
}

.appointment-form button {
  width: 100%;
  padding: 14px;
  font-size: 1rem;
}

.appointment-form button.submitting {
  pointer-events: none;
  opacity: 0.8;
}

.spinner {
  display: inline-block;
  width: 15px;
  height: 15px;
  border: 2px solid rgba\(13,27,42,0.3\);
  border-top-color: var\(--text-on-accent\);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
  margin-right: 8px;
  vertical-align: middle;
}

@keyframes spin { to { transform: rotate\(360deg\); } }

/* Success */
.form-success {
  text-align: center;
  padding: 40px 20px;
}

.success-icon {
  width: 72px;
  height: 72px;
  background: var\(--green\);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2.5rem;
  color: white;
  margin: 0 auto 20px;
}

.form-success h3 {
  font-size: 1.8rem;
  color: var\(--text-heading\);
  margin-bottom: 12px;
}

.form-success p {
  color: var\(--text-secondary\);
  font-size: 1rem;
}

@media \(max-width: 900px\) {
  .appointment-layout { grid-template-columns: 1fr; gap: 40px; }
}

@media \(max-width: 600px\) {
  .form-row { grid-template-columns: 1fr; }
  .appointment-content { padding: 28px 20px; }
}
